Bash(generate-icons.js << 'EOF'
const fs = require\('fs'\);
const { createCanvas, loadImage } = require\('canvas'\);

const sizes = [72, 96, 128, 144, 152, 192, 384, 512];

function createHourglassSVG\(size\) {
    return `
        <svg width="${size}" height="${size}" viewBox="0 0 512 512" xmlns="http://www.w3.org/2000/svg">
            <rect x="96" y="64" width="320" height="32" rx="8" fill="#000000"/>
            <rect x="96" y="416" width="320" height="32" rx="8" fill="#000000"/>
            <path d="M 128 96 L 256 256 L 384 96 Z" fill="none" stroke="#000000" stroke-width="24" stroke-linejoin="round"/>
            <path d="M 128 416 L 256 256 L 384 416 Z" fill="none" stroke="#000000" stroke-width="24" stroke-linejoin="round"/>
            <path d="M 160 128 L 256 224 L 352 128 L 160 128 Z" fill="#000000"/>
            <circle cx="256" cy="384" r="48" fill="#000000"/>
        </svg>
    `;
}

async function generateIcon\(size\) {
    const canvas = createCanvas\(size, size\);
    const ctx = canvas.getContext\('2d'\);

    const svgBuffer = Buffer.from\(createHourglassSVG\(size\)\);
    const img = await loadImage\(svgBuffer\);

    ctx.drawImage\(img, 0, 0, size, size\);

    const buffer = canvas.toBuffer\('image/png'\);
    fs.writeFileSync\(`icons/icon-${size}x${size}.png`, buffer\);
    console.log\(`Generated icon-${size}x${size}.png`\);
}

async function generateAll\(\) {
    for \(const size of sizes\) {
        await generateIcon\(size\);
    }
    console.log\('All icons generated!'\);
}

generateAll\(\).catch\(console.error\);
EOF)
